     */
    public static Boolean checkDictCase(Object dict) {
        if (dict instanceof Map) {
            Map m = (Map) dict;
            if (m.size() == 0) {
                return false;
            }
            boolean allLower = true;
            boolean allUpper = true;
            for (Object k : m.keySet()) {
                if (k instanceof String) {
                    String s = (String) k;
