	@Test
	void testOrderByPoints() {
		List<Object> nums = List.of(1, 11, -1, -11, -12);
		List<Object> expected = List.of(-1, -11, 1, -12, 11);
		assertEquals(expected, OrderByPoints.orderByPoints(nums));
	}
	
