package ui;

import java.util.*;

/**
 * Provides only a public static method
 * to offer UI resources, such as menu labels,
 * icons, toolbar icons, and etc.
 *
 * @author Allen Chue
 */
public class UIResources {

    private static ResourceBundle resources = null;

    static {
        try {
            resources = ResourceBundle.getBundle("ui.resources.ichat", //$NON-NLS-1$
            Locale.getDefault());
        } catch (MissingResourceException e) {
            //$NON-NLS-1$
            System.out.println("Cannot load resource file.");
            System.exit(1);
        }
    }

    public static String getString(String key) {
        return resources.getString(key);
    }
}

