            return null;
        File fi = new File(f);
        String foundE = "target" + File.separatorChar + "MANIFEST.MF";
        if (new File(fi, foundE).exists()) {
            return new URL("file:" + fi + File.separatorChar + foundE);
        } else {
            return searchMF(fi.getParent());
        }
    }
