   */
    public void setEditor(final String editor) {
        this.editor = editor;
    }

    /**
     * {@inheritDoc}
     */
    public boolean isRedirect() {
        Pattern pattern = Pattern.compile("#(.*)redirect(.*)", Pattern.CASE_INSENSITIVE);
        if (pattern.matcher(text).matches()) {
            return true;
        }
        return false;
    }

    /**
