    public void setEditTimestamp(String editTimestamp) throws ParseException {
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
        try {
            setEditTimestamp(sdf.parse(editTimestamp));
        } catch (ParseException e) {
