        throw new IllegalStateException();
    }

    public SimpleArticle readData(String name) {
        throw new IllegalStateException();
    }

    public void writeContent(SimpleArticle sa) {
        throw new IllegalStateException();
    }

    public void delete(String title) {
        throw new IllegalStateException();
    }
}

