                loginChangeVersion = false;
            } catch (JwbfException e) {
                log.error(e.getClass().getName() + e.getLocalizedMessage());
                throw new IllegalStateException(e.getLocalizedMessage());
            }
            log.debug("Version is: " + version.name());
        }
        return version;
    }

