    //check file age
    public boolean isAgeGood(File file) {
        //      log.debug("is good - in");
        Date now = new Date();
        if (file != null) {
            long fileAge = file.lastModified();
            long timeNow = now.getTime();
            long timeDiff = timeNow - fileAge;
            if (file.length() < 1000) {
                //        log.debug("File is Bad");
                return false;
            }
            if (timeDiff < oldestAge) {
                //        log.debug("Good");
                return true;
            } else {
