    //delete file
    public void deleteFile(String fileName) {
        //      log.debug("In delete");
        boolean deleted;
        File file = new File(fileName);
        if (file != null) {
            if (file.exists()) {
                deleted = file.delete();
                //            log.debug("deleted? " + deleted);
                //             log.debug("delete file");
            }
        }
        //     log.debug("out of delete");
    }

    //check file age
    public boolean isAgeGood(File file) {
        //      log.debug("is good - in");
        Date now = new Date();
        if (file != null) {
            long fileAge = file.lastModified();
            long timeNow = now.getTime();
            long timeDiff = timeNow - fileAge;
            if (file.length() < 1000) {
                //        log.debug("File is Bad");
                return false;
            }
            if (timeDiff < oldestAge) {
                //        log.debug("Good");
                return true;
            } else {
                //         log.debug("bad");
                return false;
            }
        } else {
            //         log.debug("bad file is null");
            return false;
        }
    }

    //rename file
    public void renameFile(String oldFileName, String newFileName) {
        boolean renamed;
        //      log.debug("rename - in");
        File file = new File(oldFileName);
        File newFile = new File(newFileName);
        try {
            if (file != null) {
                //          log.debug("file isn't null");
                if (file.exists()) {
                    //             log.debug("file exists rename it ");
                    renamed = file.renameTo(newFile);
                    //              log.debug("renamed? = " + renamed);
                }
            }
        } catch (Exception e) {
            //        log.error(e.toString());
        }
        //      log.debug("rename - out");
    }

