        for (DotNode node : nodes.values()) {
            connectors.addAll(finder.getRelatedConnectors(node.getTable(), includeImplied));
        }
        markExcludedColumns(nodes, stats.getExcludedColumns());
        for (DotNode node : nodes.values()) {
