                    } else {
                        if (logging)
                            logger.fine("Excluding schema " + schema + ": matches \"" + schemaRegex + "\" but contains no tables");
                    }
                } catch (SQLException ignore) {
                } finally {
                    if (rs != null)
                        rs.close();
                }
            } else {
                if (logging)
                    logger.fine("Excluding schema " + schema + ": doesn't match \"" + schemaRegex + '"');
            }
        }
        return new ArrayList<String>(schemas);
    }

