                status = p.getNaStatus();
            } else {
                status = p.getOpNuStatus();
            }
            // Customizing holen
            c = cService.getByNaOpNuUID(naOpNuUID);
        } else {
            // Customizing holen
            c = cService.getByPaUID(paUID);
            status = p.getPaStatus();
        }
        // Status zur�ckgeben
        if (c != null) {
            return c.getStatus() && status;
        }
        return false;
