            if (opNu) {
                logger.debug("Gesetzte UID: Operativer Nutzen");
                projekt.setOpNuUID(naOpNuUID);
                // Anzahl Resultate holen
                naOpNu.setNutzenattraktivitaet(false);
                naOpNu.setOperativerNutzen(true);
