            } else
                label = "label.objektiv.attraktiv.management.unattraktiv";
        }
        if (pa >= 1.5 && pa <= 3.0 && na >= 1.5 && na <= 3.0) {
            if (opNu != null) {
                if (opNu >= 0 && opNu < 1.5 && diff >= 0.3)
