    public List<Kategorie> setStatus(HttpServletRequest request, Integer mandantId, Integer naOpNuId, boolean isNa) {
        Kategorie k = new Kategorie();
        k.setMandantId(mandantId);
        k.setNutzenattraktivitaetOperativerNutzenId(naOpNuId);
