    public void populateDirekteGewichtung(HttpServletRequest request, FragenForm form) {
        // Ben�tigte ids holen
        HttpSession session = request.getSession();
        Integer customizingId = (Integer) session.getAttribute(Constants.CUSTOMIZING_ID);
