    public List<Nutzenkriterium> setStatus(HttpServletRequest request, List<Kategorie> kategorien, Integer mandantId, Integer naOpNuId, boolean isNa) {
        boolean ok = true;
        for (Kategorie k : kategorien) {
            Nutzenkriterium nk = new Nutzenkriterium();
            nk.setKategorieId(k.getKategorieId());
            nk.setNutzenattraktivitaetOperativerNutzenId(naOpNuId);
