        it = list.iterator();
        while (it.hasNext()) {
            NutzenkriteriumLine nl = (NutzenkriteriumLine) it.next();
            Nutzenkriterium n = new Nutzenkriterium();
            n.setNutzenkriteriumId(nl.getNutzenkriteriumId());
            n.setNutzenattraktivitaetOperativerNutzenId(naOpNuId);
