     */
    public boolean setGewichtungStatus(HttpServletRequest request, List<Nutzenkriterium> kriterien, Integer naOpNuId, boolean isNa) {
        HttpSession session = request.getSession();
        // Gewichtung durch Fragebogen?
        List<Auswahlfeld> gewichtungen = dao.getGewichtungStatus(naOpNuId);
        if (gewichtungen != null && gewichtungen.size() > 0) {
            logger.debug("gewichtungen.size()=" + gewichtungen.size() + " bei naOpNuId=" + naOpNuId);
            if (isNa) {
