     */
    private void insertAssignments(Auswahlfeld a, int type, DynaActionForm form, int levels) {
        // f�r jeden Level einen Eintrag des jeweiligen Auswahlfelds setzen
        for (int i = 1; i <= levels; i++) {
            String name = Constants.SELECTBOX + "_" + type + "_" + i;
