     * @param levels              die zu speichernden levels
     */
    private void insertNaOpNuAssignment(Auswahlfeld a, int type, HttpServletRequest request, DynaActionForm form, int levels) {
        // Nutzenkriterien holen
        Nutzenkriterium kriterium = new Nutzenkriterium();
        kriterium.setNutzenattraktivitaetOperativerNutzenId(a.getNutzenattraktivitaetOperativerNutzenId());
        kriterium.setMandantId(a.getMandantId());
        List<Nutzenkriterium> nutzenkriterien = nService.getAssignments(kriterium);
        // alte Zuweisungen l�schen
        Integer kategorieId = (Integer) form.get(Constants.KATEGORIE_ID);
        for (Nutzenkriterium n : nutzenkriterien) {
            logger.debug("kategorie: " + n.getKategorieId() + ", " + kategorieId);
            if (kategorieId.equals(n.getKategorieId()) || type == Constants.GEWICHTUNG) {
                a.setNutzenkriteriumId(n.getNutzenkriteriumId());
                logger.debug("L�sche Zuweisungen: " + " eintrittswahrscheinlichkeit=" + a.getEintrittswahrscheinlichkeit() + ", eintrittszeitpunkt=" + a.getEintrittszeitpunkt() + ", abstufung=" + a.getAbstufung() + ", gewichtung=" + a.getGewichtung() + ", nutzenkriteriumId=" + a.getNutzenkriteriumId() + ", nutzenattraktivitaetOperativerNutzenId=" + a.getNutzenattraktivitaetOperativerNutzenId());
                dao.deleteNaOpNuAssignmentsByNutzenkriterium(a);
            }
        }
        // f�r jeden Level einen Eintrag des jeweiligen Auswahlfelds setzen
        for (int i = 1; i <= levels; i++) {
            a.setLevel(i);
            for (Nutzenkriterium n : nutzenkriterien) {
                a.setNutzenkriteriumId(n.getNutzenkriteriumId());
                String name = Constants.SELECTBOX + "_" + type + "_" + i;
                // Gewichtungen werden nicht mehr pro Nutzenkriterium bestimmt ...
                // Design change
                if (type != Constants.GEWICHTUNG) {
                    name += "_" + n.getNutzenkriteriumId();
                }
                logger.debug("Hole Auswahlfeld " + name + " vom Formular");
                String aIdStr = request.getParameter(name);
                logger.debug("aIdStr=" + aIdStr);
                Integer aId;
                // neue Zuweisung erstellen
                if (aIdStr != null && (aId = new Integer(aIdStr)) != null) {
                    a.setAuswahlfeldId(aId);
                    logger.debug("Erstelle Zuweisung: auswahlfeldId=" + aId + ", nutzenkriteriumId=" + a.getNutzenkriteriumId() + ", nutzenattraktivitaetOperativerNutzenId=" + a.getNutzenattraktivitaetOperativerNutzenId());
                    dao.insertNaOpNuAssignment(a);
                }
            }
        }
    }

    /**
