    /**
     * Speichert ein neues Auswahlfeld und f�gt es dem aktuellen
     * Customizing zu.
     *
     * @param request                   der HttpServletRequest
     * @param form                      das DynaActionForm mit Daten des Auswahlfelds
     * @param errors                    ActionMessages Objekt zur Ausgabe von
     *                                  Feld-bezogenen Fehlermeldungen
     * @param messages                  ActionMessages Objekt zur Ausgabe von
     *                                  globalen Fehlermeldungen
     * @return                          true bei Erfolg, sonst false
     */
    public boolean add(HttpServletRequest request, DynaActionForm form, ActionMessages errors, ActionMessages messages) {
        Integer mandantId = (Integer) request.getSession().getAttribute(Constants.MANDANT_ID);
        // Formulardaten holen
        String[] names = (String[]) form.get("name");
        Integer[] types = (Integer[]) form.get("type");
        Integer[] lvls = (Integer[]) form.get("lvl");
        String name = null;
        Integer pos = null;
        for (int i = 0; i < names.length; i++) {
            logger.debug("names[" + i + "]=" + names[i]);
            if (names[i] != null && !names[i].equals("")) {
                name = names[i];
                pos = i;
            }
        }
        for (int i = 0; i < types.length; i++) {
            logger.debug("types[" + i + "]=" + types[i]);
        }
        for (int i = 0; i < lvls.length; i++) {
            logger.debug("lvls[" + i + "]=" + lvls[i]);
        }
        // Formulardaten �berpr�fen
        if (name == null) {
            logger.debug("Name nicht vorhanden.");
            errors.add("name", new ActionMessage("errors.required", "Name"));
            return false;
        }
        Integer type = types[pos];
        Integer lvl = lvls[pos];
        Auswahlfeld af = new Auswahlfeld();
        af.setMandantId(mandantId);
        af.setName(name);
        af.setLevel(lvl);
        setType(af, type);
        // Auf duplikate pr�fen
        Auswahlfeld a = dao.getByName(af);
        Boolean match = false;
        if (a != null) {
            if (a.getAbstufung() && type == Constants.ABSTUFUNG) {
                match = true;
            }
            if (a.getGewichtung() && type == Constants.GEWICHTUNG) {
                match = true;
            }
            if (a.getEintrittswahrscheinlichkeit() && type == Constants.EINTRITTSWAHRSCHEINLICHKEIT) {
                match = true;
            }
            if (a.getEintrittszeitpunkt() && type == Constants.EINTRITTSZEITPUNKT) {
                match = true;
            }
        }
        if (a != null && match) {
            logger.debug(name + " ist ein Duplikat");
            messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage("errors.duplicate", "Eintrag"));
            return false;
        }
        // Auswahlfeld einf�gen
        dao.insert(af);
        return true;
    }

    /**
     * Kopiert alle zugewiesenen Auswahlfelder eines Customizings
     * in ein neues.
     *
     * @param paId                          die Id der aktuellen Projektattraktivit�t
     * @param newPaId                       die Id der neuen Projektattraktivit�t
     * @param naOpNuId                      die Id der aktuellen Nutzenattraktivit�t
     *                                      bzw. des aktuellen Operativen Nutzens
     * @param newNaOpNuId                   die Id der neuen Nutzenattraktivit�t
     *                                      bzw. des neuen Operativen Nutzens
     * @param type                          der Typ des Auswahlfelds
     */
    public void copy(Integer paId, Integer newPaId, Integer naOpNuId, Integer newNaOpNuId, Integer type) {
        Auswahlfeld a = new Auswahlfeld();
        // Typ setzen
        this.setType(a, type);
        // Projektattraktivit�t
        if (paId != null) {
            a.setProjektattraktivitaetId(paId);
            List<Auswahlfeld> afs = dao.getSelectedByPaId(a);
            for (Auswahlfeld af : afs) {
                af.setProjektattraktivitaetId(newPaId);
                dao.insertAssignment(af);
            }
        } else // Nutzenattraktivit�t oder Operativer Nutzen
        {
            a.setNutzenattraktivitaetOperativerNutzenId(naOpNuId);
            List<Auswahlfeld> afs = dao.getSelectedByNaOpNuId(a);
            for (Auswahlfeld af : afs) {
                af.setNutzenattraktivitaetOperativerNutzenId(newNaOpNuId);
