    public boolean setStufenStatus(HttpServletRequest request, Integer naOpNuId, boolean isNa) {
        // Zugewiesene Nutzenkriterien holen
        HttpSession session = request.getSession();
        Integer mandantId = (Integer) session.getAttribute(Constants.MANDANT_ID);
