     */
    private void put(HttpServletRequest request, Auswahlfeld a, int type, int levels) {
        // setze f�r jeden Level ein Auswahlfeld
        for (int i = 1; i <= levels; i++) {
            a.setLevel(i);
            // Inhalte der Auswahlfelder holen
            List<Auswahlfeld> list = dao.getAuswahlFelder(a);
