    /**
     * Setzt die Verkn�pfungen zu diesem Auswahlfeld in den Request.
     * Ein Auswahlfeld kann mit Customizings verkn�pft sein. Diese
     * Methode wird bei der L�schabfrage verwendet.
     *
     * @param request                   der HttpServletRequest
     * @param af                        das zu l�schende Auswahlfeld
     */
    public void linkedTo(HttpServletRequest request, Auswahlfeld af) {
        HashMap<String, List> map = new HashMap<String, List>();
        // Customizings suchen, die dieses Detailziel ben�tigen
        List<Customizing> customizings = dao.getLinkedPaCustomizings(af);
        customizings.addAll(dao.getLinkedNaOpNuCustomizings(af));
        if (customizings.size() > 0) {
            for (Customizing c : customizings) {
                if (c.getStatus()) {
                    request.setAttribute("active", true);
                    break;
                }
            }
            map.put("Customizings", customizings);
        }
        request.setAttribute("linked", map);
    }

    /**
     * L�scht ein Auswahlfeld. Falls es mit inaktiven Customizings
     * verkn�ft ist, werden diese ebenfalls gel�scht. Wenn aktive Customizings
     * verkn�ft sind, wird das Auswahlfeld nicht gel�scht.
     *
     * @param request                   der HttpServletRequest
     * @param af                        das zu l�schende Auswahlfeld
     */
    public void delete(HttpServletRequest request, Auswahlfeld af) {
        // Verkn�pfungen pr�fen
        linkedTo(request, af);
        Boolean active = (Boolean) request.getAttribute("active");
        // Aktives Customizing verkn�ft. Dies sollte nicht passieren ausser bei
        // einem direkten URL-Aufruf ...
        if (active != null && active) {
            logger.debug("Aktives Customizing verkn�pft, Auswahlfeld wird nicht gel�scht");
            return;
        }
        // Customizings l�schen
        List<Customizing> customizings = dao.getLinkedPaCustomizings(af);
        customizings.addAll(dao.getLinkedNaOpNuCustomizings(af));
        for (Customizing c : customizings) {
            logger.debug("L�sche Customizing " + c.getCustomizingId());
            customizingService.cascadeDelete(request, c);
        }
        // L�schen des Auswahlfelds
        dao.delete(af);
    }

