            if (isNa) {
                logger.debug("GEWICHTUNGEN_NA=true");
                session.setAttribute(Constants.GEWICHTUNGEN_NA, true);
            } else {
                logger.debug("GEWICHTUNGEN_OP_NU=true");
                session.setAttribute(Constants.GEWICHTUNGEN_OP_NU, true);
