            return true;
        } else // Direkte Gewichtung?
        if (kriterien != null) {
            logger.debug("direkte Gewichtung bei naOpNuId=" + naOpNuId);
            for (Nutzenkriterium k : kriterien) {
                logger.debug("nutzenkriteriumId=" + k.getNutzenkriteriumId());
