        // f�r jeden Level einen Eintrag des jeweiligen Auswahlfelds setzen
        for (int i = 1; i <= levels; i++) {
            a.setLevel(i);
            for (Nutzenkriterium n : nutzenkriterien) {
                a.setNutzenkriteriumId(n.getNutzenkriteriumId());
