    private void insertNaOpNuAssignment(Auswahlfeld a, int type, HttpServletRequest request, DynaActionForm form, int levels) {
        // Nutzenkriterien holen
        Nutzenkriterium kriterium = new Nutzenkriterium();
        kriterium.setNutzenattraktivitaetOperativerNutzenId(a.getNutzenattraktivitaetOperativerNutzenId());
        kriterium.setMandantId(a.getMandantId());
        List<Nutzenkriterium> nutzenkriterien = nService.getAssignments(kriterium);
