                    logger.debug("Erstelle Zuweisung: auswahlfeldId=" + aId + ", nutzenkriteriumId=" + a.getNutzenkriteriumId() + ", nutzenattraktivitaetOperativerNutzenId=" + a.getNutzenattraktivitaetOperativerNutzenId());
