            return false;
        } else // �berpr�ft den Status des Customizings Projektattraktivit�t
        if (name.equals(Constants.PROJEKTATTRAKTIVITAET)) {
            if (checkPartialState(paEntries)) {
                logger.debug("Projektattraktivitaet ok");
                return true;
            }
            return false;
        } else // �berpr�ft den Status des Customizings Nutzenattraktivit�t
        if (name.equals(Constants.NUTZENATTRAKTIVITAET)) {
            if (checkPartialState(naEntries)) {
                logger.debug("Nutzenattraktivitaet ok");
                return true;
            }
            return false;
        } else // �berpr�ft den Status des Customizings Operativer Nutzen
        if (name.equals(Constants.OPERATIVER_NUTZEN)) {
            if (checkPartialState(opNuEntries)) {
                logger.debug("Operativer Nutzen ok");
                return true;
            }
            return false;
        }
        // �berpr�ft den Status eines spezifizierten Navigationspunkts
        for (int i = 0; i < entries.length; i++) {
            // Status-Flag �berpr�fen
            String entry = entries[i];
            if (name.equals(entry) && checkFlag(entry)) {
