    private boolean checkFlag(String entry) {
        // Status-Flag aus der Session holen
        Object attribute = null;
        if (request != null) {
            attribute = request.getSession().getAttribute(entry);
        } else {
            attribute = pageContext.getSession().getAttribute(entry);
