package nu.staldal.lagoon.producer;

import java.io.*;
import java.util.Properties;
import javax.xml.transform.*;
import javax.xml.transform.sax.*;
import javax.xml.transform.stream.StreamResult;
import org.xml.sax.*;
import nu.staldal.lagoon.core.*;
import nu.staldal.xmlutil.ContentHandlerFixer;
import nu.staldal.xodus.*;

public class XMLFormatter extends Format {

    private Properties outputProperties;

    public void init() throws LagoonException {
        outputProperties = new Properties();
        String method = getParam("method");
        if (method == null)
            method = "XML";
        int _html;
        String html = getParam("html");
        if (html == null)
            _html = 1;
        else if (html.equals("transitional"))
            _html = 1;
        else if (html.equals("frameset"))
            _html = 2;
        else if (html.equals("strict"))
            _html = 3;
        else
            throw new LagoonException("Unknown html variant");
        if (method.equals("XML")) {
            outputProperties.setProperty(OutputKeys.METHOD, "xml");
            outputProperties.setProperty(OutputKeys.ENCODING, "UTF-8");
        } else if (method.equals("HTML")) {
            outputProperties.setProperty(OutputKeys.METHOD, "html");
            switch(_html) {
                case 1:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD HTML 4.01 Transitional//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/html4/loose.dtd");
                    break;
                case 2:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD HTML 4.01 Frameset//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/html4/frameset.dtd");
                    break;
                case 3:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD HTML 4.01//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/html4/strict.dtd");
                    break;
            }
            outputProperties.setProperty(OutputKeys.ENCODING, "iso-8859-1");
        } else if (method.equals("XHTML")) {
            outputProperties.setProperty(OutputKeys.METHOD, "xhtml");
            switch(_html) {
                case 1:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD XHTML 1.0 Transitional//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd");
                    break;
                case 2:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD XHTML 1.0 Frameset//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/xhtml1/DTD/xhtml1-frameset.dtd");
                    break;
                case 3:
                    outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, "-//W3C//DTD XHTML 1.0 Strict//EN");
                    outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd");
                    break;
            }
            outputProperties.setProperty(OutputKeys.ENCODING, "UTF-8");
        } else if (method.equals("TEXT")) {
            outputProperties.setProperty(OutputKeys.METHOD, "text");
            outputProperties.setProperty(OutputKeys.ENCODING, "iso-8859-1");
        } else
            throw new LagoonException("Unknown serializing method");
        String enc = getParam("encoding");
        if (enc != null)
            outputProperties.setProperty(OutputKeys.ENCODING, enc);
        String docPub = getParam("doctype-public");
        if (docPub != null)
            outputProperties.setProperty(OutputKeys.DOCTYPE_PUBLIC, docPub);
        String docSys = getParam("doctype-system");
        if (docSys != null)
            outputProperties.setProperty(OutputKeys.DOCTYPE_SYSTEM, docSys);
        String indent = getParam("indent");
        if (indent != null)
            outputProperties.setProperty(OutputKeys.INDENT, "yes");
        else
            outputProperties.setProperty(OutputKeys.INDENT, "no");
        String omitXmlDeclaration = getParam("omit-xml-declaration");
        if (omitXmlDeclaration != null)
            outputProperties.setProperty(OutputKeys.OMIT_XML_DECLARATION, "yes");
        else
            outputProperties.setProperty(OutputKeys.OMIT_XML_DECLARATION, "no");
    }

    public void start(OutputStream bytes, Target target) throws IOException, SAXException {
        Serializer ser;
        try {
            ser = Serializer.createSerializer(new StreamResult(bytes), outputProperties);
        } catch (IllegalArgumentException e) {
            throw new LagoonException(e.getMessage());
        }
        getNext().start(ser, target);
    }

    public boolean hasBeenUpdated(long when) throws LagoonException, IOException {
        return getNext().hasBeenUpdated(when);
    }
}

