package nu.staldal.lagoon.producer;

import java.io.*;
import java.util.*;
import org.xml.sax.*;
import nu.staldal.lsp.*;
import nu.staldal.lsp.compiler.*;
import nu.staldal.lagoon.core.*;
import nu.staldal.util.Utils;

public class LSPTransformer extends Transform {

    private static final boolean DEBUG = false;

    private LSPCompiler compiler;

    private LSPPage theCompiledPage;

    private HashMap params;

    private String pageName;

    public void init() throws LagoonException, IOException {
        compiler = new LSPCompiler();
        pageName = Utils.encodePathAsIdentifier(getEntryName()) + "_" + getPosition();
        theCompiledPage = loadLSPPage();
        if (DEBUG)
            if (theCompiledPage == null)
                System.out.println("No compiled page found");
        params = new HashMap();
        for (Enumeration e = getParamNames(); e.hasMoreElements(); ) {
            String paramName = (String) e.nextElement();
            params.put(paramName, getParam(paramName));
        }
    }

    private LSPPage loadLSPPage() throws LagoonException {
        try {
            Class theCompiledPageClass = getContext().loadClassFromRepository("_LSP_" + pageName);
            LSPPage thePage = (LSPPage) theCompiledPageClass.newInstance();
            return thePage;
        } catch (ClassNotFoundException e) {
            return null;
        } catch (InstantiationException e) {
            throw new LagoonException(e.getMessage());
        } catch (IllegalAccessException e) {
            throw new LagoonException(e.getMessage());
        }
    }

    private boolean sourceUpdated(long when) throws LagoonException, IOException {
        if (DEBUG)
            System.out.println("Checking compile dynamic");
        if (theCompiledPage.isCompileDynamic())
            return true;
        if (DEBUG)
            System.out.println("Checking next");
        if (getNext().hasBeenUpdated(when)) {
            return true;
        }
        if (DEBUG)
            System.out.println("Checking imported files");
        String[] importedFiles = theCompiledPage.getCompileDependentFiles();
        for (int i = 0; i < importedFiles.length; i++) {
            String f = importedFiles[i];
            if (DEBUG)
                System.out.println("Checking imported file: " + f);
            if (getSourceMan().fileHasBeenUpdated(f, when)) {
                return true;
            }
        }
        return false;
    }

    private void readSource(XMLStreamProducer next, final Target target) throws SAXException, IOException {
        if (DEBUG)
            System.out.println("LSP compile");
        OutputStream out = null;
        try {
            ContentHandler ch = compiler.startCompile(pageName, new URLResolver() {

                public void resolve(String url, ContentHandler ch) throws IOException, SAXException {
                    getSourceMan().getFileAsSAX(url, ch, target);
                }
            });
            next.start(ch, target);
            out = getContext().storeClassInRepository("_LSP_" + pageName);
            compiler.finishCompile(out);
            out.close();
        } catch (SAXException e) {
            if (out != null) {
                out.close();
                getContext().deleteClassInRepository("_LSP_" + pageName);
            }
            throw e;
        }
        getContext().reloadClasses();
        theCompiledPage = loadLSPPage();
        if (theCompiledPage == null) {
            throw new LagoonException("Unable to load compiled page");
        }
    }

    public void start(ContentHandler ch, final Target target) throws IOException, SAXException {
        if (target instanceof FileTarget && ((FileTarget) target).isWildcard())
            throw new LagoonException("Cannot use with wildcard pattern");
        if ((theCompiledPage == null) || sourceUpdated(theCompiledPage.getTimeCompiled())) {
            readSource(getNext(), target);
        }
        ch.startDocument();
        theCompiledPage.execute(ch, params, getContext());
        ch.endDocument();
    }

    public boolean hasBeenUpdated(long when) throws LagoonException, IOException {
        // always rebuild (but not always recompile)
        return true;
    }
}

