package nu.staldal.lagoon.util;

public final class TemplateProcessor {

    private static void throwIllegalTemplate(String template) throws TemplateException {
        if (template.length() > 64)
            throw new TemplateException();
        else
            throw new TemplateException(template);
    }

    public static String processTemplate(char left, char right, char quot1, char quot2, String template, ExpressionEvaluator evaluator) throws TemplateException {
        if (evaluator == null)
            throw new NullPointerException("evaluator is null");
        int len = (template.length() > 128) ? template.length() + 64 : template.length() * 2;
        StringBuffer sb = new StringBuffer(len);
        StringBuffer expr = null;
        char quote = 0;
        char brace = 0;
        for (int i = 0; i < template.length(); i++) {
            char c = template.charAt(i);
            if (expr == null) {
                if (c == left) {
                    if (brace == 0) {
                        brace = left;
                    } else if (brace == left) {
                        sb.append(left);
                        brace = 0;
                    } else if (brace == right) {
                        throwIllegalTemplate(template);
                    }
                } else if (c == right) {
                    if (brace == 0) {
                        brace = right;
                    } else if (brace == right) {
                        sb.append(right);
                        brace = 0;
                    } else if (brace == left) {
                        throwIllegalTemplate(template);
                    }
                } else {
                    if (brace == left) {
                        expr = new StringBuffer();
                        expr.append(c);
                        brace = 0;
                    } else if (brace == right) {
                        throwIllegalTemplate(template);
                    } else {
                        sb.append(c);
                    }
                }
            } else // expr != null
            {
                if (c == quot1 || c == quot2) {
                    expr.append(c);
                    if (quote == 0) {
                        quote = c;
                    } else if (quote == c) {
                        quote = 0;
                    }
                } else if (c == right) {
                    if (quote == 0) {
                        String exp = expr.toString();
                        String res;
                        try {
                            res = evaluator.eval(exp);
                        } catch (Exception e) {
                            throw new TemplateException(e);
                        }
                        sb.append(res);
                        expr = null;
                    } else {
                        expr.append(c);
                    }
                } else {
                    expr.append(c);
                }
            }
        }
        if (brace != 0) {
            throwIllegalTemplate(template);
        }
        return sb.toString();
    }
}

