        else if (method.equals("text"))
            isText = true;
        else if (method.equals("html"))
            isHtml = true;
        else
            throw new IllegalArgumentException("Unknown output method: " + method);
        version = outputProps.getProperty(OutputKeys.VERSION);
        if (version == null)
