    public String toString() {
        String str = "";
        if (destinationPoint == null) {
            str = "Laufweg nicht definiert";
        } else {
            str = "Laufweg zu Position (" + destinationPoint.getCurrent_x() + "," + destinationPoint.getCurrent_y() + ")";
        }
        return str;
    }

