    public boolean isSaved() {
        boolean changed = false;
        if (lastSavedModel != null) {
            //Spielzugnamen vergleichen
            if (lastSavedModel.getMoveName() != null) {
                if (getMoveName() == null || !lastSavedModel.getMoveName().equals(getMoveName())) {
                    return false;
                }
            } else if (getMoveName() != null) {
                //alt: kein Name <-> neu: Name vorhanden
                return false;
            }
            //Ueberpr체fen ob Angreifer und Verteidiger unterschiedlich
            // zuerst Spielerlisten vergleichen
            if (changedPlayerList(getOffenders().toArray(new Player[getOffenders().size()]), lastSavedModel.getOffenders().toArray(new Player[lastSavedModel.getOffenders().size()])) || changedPlayerList(getDefenders().toArray(new Player[getDefenders().size()]), lastSavedModel.getDefenders().toArray(new Player[lastSavedModel.getDefenders().size()]))) {
                changed = true;
            } else {
                List<MoveEvent> newEvents = getEvents();
                List<MoveEvent> oldEvents = lastSavedModel.getEvents();
                if (newEvents.size() == oldEvents.size()) {
                    for (int i = 0; i < newEvents.size(); i++) {
                        if (!newEvents.get(i).equals(oldEvents.get(i))) {
                            changed = true;
                            break;
                        }
                    }
                } else {
                    changed = true;
                }
            }
            if (!changed && (getFirstBallOwner() != null || lastSavedModel.getFirstBallOwner() != null)) {
                changed = (getFirstBallOwner() == null) ^ (lastSavedModel.getFirstBallOwner() == null);
                if (!changed) {
                    changed = !getFirstBallOwner().equals(lastSavedModel.getFirstBallOwner());
                }
            }
            // wurde Kommentar ge채ndert?
            if (!changed) {
                if (comment == null || comment.equals("")) {
                    if (lastSavedModel.getComment() != null && !lastSavedModel.getComment().equals("")) {
                        changed = true;
                    }
                } else if (lastSavedModel.getComment() != null) {
                    if (!lastSavedModel.getComment().equals(comment)) {
                        changed = true;
                    }
                }
            }
        }
        return !changed;
    }

    private boolean changedPlayerList(Player[] newList, Player[] oldList) {
        boolean changed = false;
        int size = newList.length;
        int sizeOld = oldList.length;
        if (size == sizeOld) {
            for (int i = 0; i < size; i++) {
                if (!newList[i].equals(oldList[i])) {
                    changed = true;
                    break;
                }
            }
        } else {
            changed = true;
        }
        return changed;
    }

