    private int determineMaxPlayerNumber(List players) {
        int max = 0;
        for (Object o : players) {
            if (o instanceof Player) {
                Player player = (Player) o;
                if (player.getPlayerNumber() > max) {
                    max = player.getPlayerNumber();
                }
            }
        }
        return max;
