    public boolean passEventDefined(int seq) {
        boolean defined = false;
        for (MoveEvent event : events) {
            if (event.getSequenceNr() == seq && event instanceof PassEvent) {
                defined = true;
                break;
            }
            if (event.getSequenceNr() > seq) {
                break;
            }
        }
        return defined;
    }

