    // DesignPatterns.
    public void setFigures(StandardEnumeration figures) throws FigureException {
        throw new FigureException(ResourceBundlePurchaser.getMessage(AbstractFigure.TRYING2SET_PARTS_OF_A_NON_COMPOSITE_FIGURE));
