    public AliasFilePath(String alias, String filePath) {
        identifier = 0;
        this.alias = alias;
        this.filePath = filePath;
        isNewRecord = false;
    }

    public AliasFilePath() {
        identifier = 0;
        alias = "";
        filePath = "";
        isNewRecord = false;
    }

    // ==========================================
