            oldAvailability = ValidationUtility.convertEmptyValueToBlank(oldAvailability);
            revisedAvailability = ValidationUtility.convertEmptyValueToBlank(revisedAvailability);
            String availabilityText = MacawMessages.getMessage("variable.availability.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, availabilityText, oldAvailability, revisedAvailability);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        boolean originalIsCoded = originalVariable.isCoded();
        boolean revisedIsCoded = revisedVariable.isCoded();
        if (originalIsCoded != revisedIsCoded) {
            String oldIsCodedValue = Boolean.toString(originalIsCoded);
