            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //detect changes in column start
        String columnStartLabelText = MacawMessages.getMessage("variable.columnStart.label");
        String originalColumnStart = originalVariable.getColumnStart();
        if (originalColumnStart == null) {
            originalColumnStart = MacawMessages.getMessage("general.fieldValue.blank");
        }
        String revisedColumnStart = revisedVariable.getColumnStart();
        if (revisedColumnStart == null) {
            revisedColumnStart = MacawMessages.getMessage("general.fieldValue.blank");
        }
        if (originalColumnStart.equals(revisedColumnStart) == false) {
            //change occured
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, columnStartLabelText, originalColumnStart, revisedColumnStart);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //detect changes in column end
        String columnEndLabelText = MacawMessages.getMessage("variable.columnEnd.label");
        String originalColumnEnd = originalVariable.getColumnEnd();
        if (originalColumnEnd.equals("") == true) {
            originalColumnEnd = MacawMessages.getMessage("general.fieldValue.blank");
        }
        String revisedColumnEnd = revisedVariable.getColumnEnd();
        if (revisedColumnEnd.equals("") == true) {
            revisedColumnEnd = MacawMessages.getMessage("general.fieldValue.blank");
        }
        if (originalColumnEnd.equals(revisedColumnEnd) == false) {
            //change occured
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, columnEndLabelText, originalColumnEnd, revisedColumnEnd);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //check alias
        String oldAlias = originalVariable.getAlias();
        String newAlias = revisedVariable.getAlias();
        if (oldAlias.equals(newAlias) == false) {
            oldAlias = ValidationUtility.convertEmptyValueToBlank(oldAlias);
            newAlias = ValidationUtility.convertEmptyValueToBlank(newAlias);
            String aliasText = MacawMessages.getMessage("variable.alias.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, aliasText, oldAlias, newAlias);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //check file name
        /**
         * 		String oldFilePath = originalVariable.getFilePath();
