    static public ArrayList<MacawChangeEvent> detectFieldChanges(User user, Variable originalVariable, Variable revisedVariable) {
        String userID = user.getUserID();
        ArrayList<MacawChangeEvent> changeEvents = new ArrayList<MacawChangeEvent>();
        String ownerVariableName = originalVariable.getDisplayName();
        //detect changes in name
        String oldName = originalVariable.getName();
        String newName = revisedVariable.getName();
        if (oldName.equals(newName) == false) {
            oldName = ValidationUtility.convertEmptyValueToBlank(oldName);
            newName = ValidationUtility.convertEmptyValueToBlank(newName);
            String nameLabelText = MacawMessages.getMessage("variable.name.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, nameLabelText, oldName, newName);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //=Changed variable {0}, field {1}, from {2} to {3}
        //detect changes in label
        String oldLabel = originalVariable.getLabel();
        String newLabel = revisedVariable.getLabel();
        if (oldLabel.equals(newLabel) == false) {
            oldLabel = ValidationUtility.convertEmptyValueToBlank(oldName);
            newLabel = ValidationUtility.convertEmptyValueToBlank(newLabel);
            String labelLabelText = MacawMessages.getMessage("variable.label.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, labelLabelText, oldLabel, newLabel);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //detect changes in year
        String oldYear = originalVariable.getYear();
        String newYear = revisedVariable.getYear();
        if (oldYear.equals(newYear) == false) {
            oldYear = ValidationUtility.convertEmptyValueToBlank(oldYear);
            newYear = ValidationUtility.convertEmptyValueToBlank(newYear);
            String yearLabelText = MacawMessages.getMessage("variable.year.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, yearLabelText, oldYear, newYear);
            MacawChangeEvent changeEvent = new MacawChangeEvent(ChangeEventType.VARIABLE, fieldChangedMessage, userID);
            changeEvent.setVariableOwnerID(originalVariable.getIdentifier());
            changeEvent.setChangedObjectIdentifier(originalVariable.getIdentifier());
            changeEvents.add(changeEvent);
        }
        //detect changes in category
        String originalCategory = originalVariable.getCategory();
        String revisedCategory = revisedVariable.getCategory();
        if (originalCategory.equals(revisedCategory) == false) {
            originalCategory = ValidationUtility.convertEmptyValueToBlank(originalCategory);
            revisedCategory = ValidationUtility.convertEmptyValueToBlank(revisedCategory);
            String categoryLabelText = MacawMessages.getMessage("variable.category.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, categoryLabelText, originalCategory, revisedCategory);
