            changeEvents.add(changeEvent);
        } else {
            String oldCleaningChoice = originalVariable.getCleaningStatus();
            String revisedCleaningChoice = revisedVariable.getCleaningStatus();
            if (oldCleaningChoice.equals(revisedCleaningChoice) == false) {
                oldCleaningChoice = ValidationUtility.convertEmptyValueToBlank(oldCleaningChoice);
                revisedCleaningChoice = ValidationUtility.convertEmptyValueToBlank(revisedCleaningChoice);
                String cleaningStatusText = MacawMessages.getMessage("variable.cleaningStatus.label");
