            changeEvents.add(changeEvent);
        }
        boolean originalIsCoded = originalVariable.isCoded();
        boolean revisedIsCoded = revisedVariable.isCoded();
        if (originalIsCoded != revisedIsCoded) {
            String oldIsCodedValue = Boolean.toString(originalIsCoded);
            String revisedIsCodedValue = Boolean.toString(revisedIsCoded);
            String isCodedLabel = MacawMessages.getMessage("variable.isCoded.label");
            String fieldChangedMessage = MacawMessages.getMessage("variable.saveChanges.valueChanged", ownerVariableName, isCodedLabel, oldIsCodedValue, revisedIsCodedValue);
