    public void setSupportingDocuments(ArrayList<SupportingDocument> supportingDocuments) {
        this.supportingDocuments = supportingDocuments;
    }

    public ArrayList<SupportingDocument> filterSupportingDocuments(User user, String documentTitleFilter, String documentCodeFilter) throws MacawException {
        if ((ValidationUtility.isEmptyValue(documentTitleFilter) == true) && (ValidationUtility.isEmptyValue(documentCodeFilter) == true)) {
            String errorMessage = MacawMessages.getMessage("supportingDocumentFilter.error");
            MacawException exception = new MacawException(MacawErrorType.NO_SUPPORTING_DOCUMENT_FILTER, errorMessage);
            throw exception;
        }
        ArrayList<SupportingDocument> documentTitleResults = filterByDocumentTitle(documentTitleFilter, supportingDocuments);
        ArrayList<SupportingDocument> documentCodeFilterResults = filterByDocumentCode(documentCodeFilter, documentTitleResults);
        ArrayList<SupportingDocument> finalResults = new ArrayList<SupportingDocument>();
        //put them in an alphabetical order
        for (SupportingDocument currentSupportingDocument : documentCodeFilterResults) {
            String currentDocumentDisplayName = currentSupportingDocument.getDisplayName();
            int insertionIndex = -1;
            int numberOfResults = finalResults.size();
            for (int i = 0; i < numberOfResults; i++) {
                String currentResultDisplayName = finalResults.get(i).getDisplayName();
                if (currentDocumentDisplayName.compareTo(currentResultDisplayName) > 0) {
                    insertionIndex = i;
                    break;
                }
            }
            SupportingDocument cloneSupportingDocument = (SupportingDocument) currentSupportingDocument.clone();
            if (insertionIndex == -1) {
                //item belongs at the end
                finalResults.add(cloneSupportingDocument);
            } else {
                finalResults.add(insertionIndex, cloneSupportingDocument);
            }
        }
        return finalResults;
    }

    private ArrayList<SupportingDocument> filterByDocumentTitle(String documentTitleFilter, ArrayList<SupportingDocument> results) {
        ArrayList<SupportingDocument> refinedResults = new ArrayList<SupportingDocument>();
        if (ValidationUtility.isEmptyValue(documentTitleFilter) == true) {
            //return everything so just return the original result list
