    public void run() {
        try {
            try {
                //Part I: Obtain the properties file that contains
                //all the startup properties
                File setupFile = new File(startupPropertiesFileName);
                if (setupFile.exists() == false) {
                    UserInterfaceFactory userInterfaceFactory = new UserInterfaceFactory();
                    JFileChooser fileChooser = userInterfaceFactory.createFileChooser();
                    int result = fileChooser.showOpenDialog(null);
                    if (result == JFileChooser.APPROVE_OPTION) {
